mapper.select:
SELECT pwd, email, pay, profile, TO_CHAR(regdate, 'YYYY.MM.DD HH24:MI') AS regdate, manage, phone
FROM user_team
WHERE id=#{id}

mapper.update:
UPDATE user_team
SET email=#{email}
WHERE id=#{id}

mapper.select:
SELECT pwd
FROM user_team
WHERE id=#{id}

mapper.update:
